// Задача для удаления файлов перед каждой задачей
gulp.task('delete-img', function () {
  return del(['dist/img/**/*']);
});

gulp.task('delete-svg', function () {
  return del(['dist/svg/**/*']);
});

gulp.task('delete-js', function () {
  return del(['dist/js/**/*']);
});

// Задача по оптимизации и переносу изображений
gulp.task('img', async function () {
  return gulp.src('src/img/**/*.{jpg,jpeg,png,gif,svg}')
    .pipe(imagemin.default([
      imagemin.gifsicle({ interlaced: true }),
      imagemin.mozjpeg({ quality: 75, progressive: true }),
      imagemin.optipng({ optimizationLevel: 5 }),
      imagemin.svgo({ plugins: [{ removeViewBox: true }] })
    ]))
    .pipe(gulp.dest('dist/img'));
});

// Задача по оптимизации и переносу SVG-файлов
gulp.task('svg', function () {
  return gulp.src('src/svg/**/*.svg')
    .pipe(svgmin())
    .pipe(gulp.dest('dist/svg'));
});

// Обработка HTML
gulp.task('html', function () {
  return gulp.src(['*.html'])
    .pipe(fileInclude({
      basePath: 'SECTIONS/'
    }))
    .pipe(fileInclude({
      basePath: 'ITEMS/'
    }))
    .pipe(gulp.dest('dist'));
});

// Обработка SCSS
gulp.task('scss', function () {
  return gulp.src('src/scss/**/*.scss')
    .pipe(scss().on('error', scss.logError))
    .pipe(cleanCSS())
    .pipe(rename({ suffix: '.min' }))
    .pipe(gulp.dest('dist/css'));
});

// Обработка JavaScript
gulp.task('js', function () {
  return gulp.src('src/js/**/*.js')
    .pipe(concat('main.js'))
    .pipe(uglify())
    .pipe(rename({ suffix: '.min' }))
    .pipe(gulp.dest('dist/js'));
});

// Отслеживание изменений
gulp.task('watch', function () {
  gulp.watch('*.html', gulp.series('html'));
  gulp.watch('SECTIONS/**/*.html', gulp.series('html'));
  gulp.watch('ITEMS/**/*.html', gulp.series('html'));
  gulp.watch('src/scss/**/*.scss', gulp.series('scss'));
  gulp.watch('src/img/**/*.{jpg,jpeg,png,gif,svg}', gulp.series('delete-img', 'img'));
  gulp.watch('src/svg/**/*.svg', gulp.series('delete-svg', 'svg'));
  gulp.watch('src/js/**/*.js', gulp.series('delete-js', 'js'));
});

// Задача по умолчанию без очистки
gulp.task('default', gulp.series('html', 'scss', 'js', 'img', 'svg', 'watch'));

// Задача для сборки релизной версии
gulp.task('build', gulp.series('clean', 'html', 'scss', 'js', 'img', 'svg'));
